SELECT distinct S2.name, N2.seq FROM station S2, network N2 WHERE S2.stop_id = N2.from_stop and (N2.route_id,N2.trip_I) = ANY (SELECT distinct N1.route_id, N1.trip_I  FROM network as N1 WHERE  N1.to_stop = 27383 AND N1.trip_I IN (SELECT distinct N.trip_I FROM station as A, network as N, route as R WHERE A.name = 'MASSY PALAISEAU' and A.stop_id = N.from_stop and R.route_id = N.route_id)) ORDER BY N2.seq ASC;